func initScript(id string) string {
	return `<script>
(function() {
	const container = document.getElementById('` + id + `');
	if (!container) return;

	initMonacoEditor('` + id + `', {
		value: container.dataset.value || '',
		language: container.dataset.language || 'lua',
		theme: container.dataset.theme || 'github-dark',
		readOnly: container.dataset.readonly === 'true',
		lineNumbers: container.dataset.linenumbers === 'true',
		minimap: container.dataset.minimap === 'true',
	});
})();
</script>`
}

func getHeight(height string) string {
	if height == "" {
		return "400px"
	}
	return height
}

func getLanguage(lang string) string {
	if lang == "" {
		return "lua"
	}
	return lang
}

func getTheme(theme string) string {
	if theme == "" {
		return "vs-dark"
	}
	return theme
}

func getLineNumbers(lineNumbers bool) bool {
	return lineNumbers
}

func boolToString(b bool) string {
	if b {
		return "true"
	}
	return "false"
}

